roduct[''recurring''][''price''] * $product[''quantity''], $product[''tax_class_id''], $this->config->get(''config_tax'')), $this->session->data[''currency'']), $product[''recurring''][''cycle''], $frequencies[$product[''recurring''][''frequency'']], $product[''recurring''][''duration'']);\r\n            					} else {\r\n            						$recurring .= sprintf($this->language->get(''text_payment_cancel''), $this->currency->format($this->tax->calculate($product[''recurring''][''price''] * $product[''quantity''], $product[''tax_class_id''], $this->config->get(''config_tax'')), $this->session->data[''currency'']), $product[''recurring''][''cycle''], $frequencies[$product[''recurring''][''frequency'']], $product[''recurring''][''duration'']);\r\n            					}\r\n            				}\r\n            \r\n            				$data[''products''][] = array(\r\n            					''cart_id''   => $product[''cart_id''],\r\n            					''thumb''     => $image,\r\n            					''name''      => $product[''name''],\r\n            					''model''     => $product[''model''],\r\n            					''option''    => $option_data,\r\n            					''recurring'' => $recurring,\r\n            					''quantity''  => $product[''quantity''],\r\n            					''stock''     => $product[''stock''] ? true : !(!$this->config->get(''config_stock_checkout'') || $this->config->get(''config_stock_warning'')),\r\n            					''reward''    => ($product[''reward''] ? sprintf($this->language->get(''text_points''), $product[''reward'']) : ''''),\r\n            					''price''     => $price,\r\n            					''total''     => $total,\r\n            					''href''      => $this->url->link(''product/product'', ''product_id='' . $product[''product_id''])\r\n            				);\r\n        				}\r\n        				\r\n        				// Gift Voucher\r\n            			$data[''vouchers''] = array();\r\n            \r\n            			if (!empty($this->session->data[''vouchers''])) {\r\n            				foreach ($this->session->data[''vouchers''] as $key => $voucher) {\r\n            					$data[''vouchers''][] = array(\r\n            						''key''         => $key,\r\n            						''description'' => $voucher[''description''],\r\n            						''amount''      => $this->currency->format($voucher[''amount''], $this->session->data[''currency'']),\r\n            						''remove''      => $this->url->link(''checkout/cart'', ''remove='' . $key)\r\n            					);\r\n            				}\r\n            			}\r\n            			\r\n            			// Totals\r\n            			$this->load->model(''extension/extension'');\r\n            \r\n            			$totals = array();\r\n            			$taxes = $this->cart->getTaxes();\r\n            			$total = 0;\r\n            			\r\n            			// Because __call can not keep var references so we put them into an array. 			\r\n            			$total_data = array(\r\n            				''totals'' => &$totals,\r\n            				''taxes''  => &$taxes,\r\n            				''total''  => &$total\r\n            			);\r\n            			\r\n            			// Display prices\r\n            			if ($this->customer->isLogged() || !$this->config->get(''config_customer_price'')) {\r\n            				$sort_order = array();\r\n            \r\n            				$results = $this->model_extension_extension->getExtensions(''total'');\r\n            \r\n            				foreach ($results as $key => $value) {\r\n            					$sort_order[$key] = $this->config->get($value[''code''] . ''_sort_order'');\r\n            				}\r\n            \r\n            				array_multisort($sort_order, SORT_ASC, $results);\r\n            \r\n            				foreach ($results as $result) {\r\n            					if ($this->config->get($result[''code''] . ''_status'')) {\r\n            						$this->load->model(''extension/total/'' . $result[''code'']);\r\n            						\r\n            						// We have to put the totals in an array so that they pass by reference.\r\n            						$this->{''model_extension_total_'' . $result[''code'']}->getTotal($total_data);\r\n            					}\r\n            				}\r\n            \r\n            				$sort_order = array();\r\n            \r\n            				foreach ($totals as $key => $value) {\r\n            					$sort_order[$key] = $value[''sort_order''];\r\n            				}\r\n            \r\n            				array_multisort($sort_order, SORT_ASC, $totals);\r\n            			}\r\n            \r\n            			$data[''totals''] = array();\r\n            		\r\n            			foreach ($totals as $total) {\r\n            				$data[''totals''][] = array(\r\n            					''title'' => $total[''title''],\r\n            					''text''  => $this->currency->format($total[''value''], $this->session->data[''currency''])\r\n            				);\r\n            			}\r\n                    }\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n        \r\n        <operation error="log">\r\n            <search ><![CDATA[$this->response->setOutput($this->load->view(''checkout/checkout'', $data));]]></search>\r\n            <add position="replace"><![CDATA[\r\n                if (isset($setting_so_onepagecheckout_general[''so_onepagecheckout_enabled'']) && $setting_so_onepagecheckout_general[''so_onepagecheckout_enabled'']) {\r\n                    $data[''forgotten''] = $this->url->link(''account/forgotten'', '''', true);\r\n                    $this->model_extension_module_so_onepagecheckout->save();\r\n                    if ($setting_so_onepagecheckout_general[''so_onepagecheckout_layout''] == 1)\r\n                        $this->response->setOutput($this->load->view(''extension/module/so_onepagecheckout/default'', $data));\r\n                    else if ($setting_so_onepagecheckout_general[''so_onepagecheckout_layout''] == 2)\r\n                        $this->response->setOutput($this->load->view(''extension/module/so_onepagecheckout/default2'', $data));\r\n                }\r\n                els